Y un <a href="http://gist.github.com/3649399">Gist</a> con el Game Loop completo como template para que hagan cosas locas:

[js]
var fernetjs = fernetjs || {};
fernetjs.juego = (function(){
  var reqAnimId,
    canvas,
    contexto,
    canvasBuffer,
    contextoBuffer;
 
  function actualizar() {
    //actualizaciones del estado
  }
 
  function dibujar() {
    contextoBuffer.clearRect(0, 0, canvas.width, canvas.height);
    
    //dibujos en el contextoBuffer
 
    contexto.clearRect(0, 0, canvas.width, canvas.height);  
    contexto.drawImage(canvasBuffer, 0, 0); 
  }
 
  function iniciarCanvas() {
    canvas = document.getElementById('canvas');
     
    canvasBuffer = document.createElement('canvas');
    canvasBuffer.width = canvas.width;
    canvasBuffer.height = canvas.height;
     
    if (canvas.getContext){
      contexto = canvas.getContext('2d');
      contextoBuffer = canvasBuffer.getContext('2d');
    } 
    else throw &quot;canvas no soportado!&quot;;
  }
 
  function loop(){
   actualizar();
   dibujar();
 
   reqAnimId = window.requestAnimationFrame(loop);
  }
 
 return {

   iniciar: function() {
     if (!canvas)
       iniciarCanvas();
  
     if(reqAnimId)
       this.detener();

     loop();
   },

   detener: function() {
      window.cancelAnimationFrame(reqAnimId);
      reqAnimId = 0;
   }
 }
 
})();

